#!/bin/sh

nohup rlai train --random-seed 12345 --agent rlai.policy_gradient.ParameterizedMdpAgent --gamma 0.9 --environment cart_pole.environment.CartPole --T 4000 --limit-to-limit-mm 940 --soft-limit-standoff-mm 108.0 --cart-width-mm 45.0 --motor-pwm-channel 0 --motor-pwm-direction-pin CkPin.GPIO21 --motor-negative-speed-is-right --cart-rotary-encoder-phase-a-pin CkPin.GPIO22 --pole-rotary-encoder-speed-phase-a-pin CkPin.GPIO5 --pole-rotary-encoder-direction-phase-a-pin CkPin.GPIO17 --pole-rotary-encoder-direction-phase-b-pin CkPin.GPIO27 --left-limit-switch-input-pin CkPin.GPIO20 --right-limit-switch-input-pin CkPin.GPIO16 --timesteps-per-second 60.0 --calibration-path ~/Desktop/cartpole-calibration.pickle --progressive-upright-led-pin CkPin.GPIO26 --falling-led-pin CkPin.GPIO19 --cart-moving-right-led-pin CkPin.MISO --balance-led-pin CkPin.MOSI --termination-led-pin CkPin.GPIO13 --balance-gamma 0.9 --failsafe-pwm-off-pin CkPin.GPIO6 --centering-range-finder-trigger-pin CkPin.GPIO23 --centering-range-finder-echo-pin CkPin.GPIO24 --train-function rlai.policy_gradient.monte_carlo.reinforce.improve --num-episodes 1000 --num-episodes-per-policy-update-plot 1 --policy rlai.policy_gradient.policies.continuous_action.ContinuousActionBetaDistributionPolicy --policy-feature-extractor cart_pole.feature_extraction.CartPolePolicyFeatureExtractor --alpha 0.00001 --update-upon-every-visit True --v-S rlai.state_value.function_approximation.ApproximateStateValueEstimator --feature-extractor cart_pole.feature_extraction.CartPolePolicyFeatureExtractor --function-approximation-model rlai.models.sklearn.SKLearnSGD --loss squared_error --sgd-alpha 0.0 --learning-rate constant --eta0 0.00001 --save-agent-path ~/Desktop/cartpole-agent.pickle --checkpoint-path ~/Desktop/cartpole-checkpoints/checkpoint.pickle --num-episodes-per-checkpoint 1 --log INFO --policy-update-plot-pdf-directory ~/Desktop/cartpole-pdfs &
